import React, { useState, useRef, useEffect } from 'react'

interface LanyardProps {
  position?: [number, number, number]
  gravity?: [number, number, number]
  fov?: number
  transparent?: boolean
}

const Lanyard: React.FC<LanyardProps> = () => {
  const [isDragging, setIsDragging] = useState(false)
  const [position, setPosition] = useState({ x: 0, y: 0 })
  const [rotation, setRotation] = useState({ x: 0, y: 0 })
  const cardRef = useRef<HTMLDivElement>(null)

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true)
    e.preventDefault()
  }

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging || !cardRef.current) return
    
    const rect = cardRef.current.getBoundingClientRect()
    const centerX = rect.left + rect.width / 2
    const centerY = rect.top + rect.height / 2
    
    const deltaX = (e.clientX - centerX) / rect.width
    const deltaY = (e.clientY - centerY) / rect.height
    
    setRotation({
      x: -deltaY * 30,
      y: deltaX * 30
    })
    
    setPosition({
      x: deltaX * 20,
      y: deltaY * 20
    })
  }

  const handleMouseUp = () => {
    setIsDragging(false)
    // Return to original position
    setRotation({ x: 0, y: 0 })
    setPosition({ x: 0, y: 0 })
  }

  useEffect(() => {
    if (isDragging) {
      const handleGlobalMouseMove = (e: MouseEvent) => {
        if (!cardRef.current) return
        
        const rect = cardRef.current.getBoundingClientRect()
        const centerX = rect.left + rect.width / 2
        const centerY = rect.top + rect.height / 2
        
        const deltaX = (e.clientX - centerX) / rect.width
        const deltaY = (e.clientY - centerY) / rect.height
        
        setRotation({
          x: -deltaY * 30,
          y: deltaX * 30
        })
        
        setPosition({
          x: deltaX * 20,
          y: deltaY * 20
        })
      }

      document.addEventListener('mousemove', handleGlobalMouseMove)
      document.addEventListener('mouseup', handleMouseUp)
      return () => {
        document.removeEventListener('mousemove', handleGlobalMouseMove)
        document.removeEventListener('mouseup', handleMouseUp)
      }
    }
  }, [isDragging])

  return (
    <div className="w-full h-full flex items-center justify-center perspective-1000">
      {/* Lanyard String */}
      <div className="absolute top-0 left-1/2 transform -translate-x-1/2 w-1 h-20 bg-gradient-to-b from-accent-purple to-accent-blue opacity-60 rounded-full"></div>
      
      {/* Lanyard Clip */}
      <div className="absolute -top-4 left-1/2 transform -translate-x-1/2 w-6 h-6 bg-gradient-to-br from-accent-purple to-accent-blue rounded-full border-2 border-white/20 shadow-lg flex items-center justify-center">
        <div className="w-2 h-2 bg-white/30 rounded-full"></div>
      </div>

      {/* ID Card */}
      <div
        ref={cardRef}
        className={`lanyard-card relative w-64 h-80 bg-gradient-to-br from-secondary-dark to-primary-dark rounded-3xl border border-white/10 shadow-2xl overflow-hidden ${
          isDragging ? 'cursor-grabbing' : 'cursor-grab'
        }`}
        style={{
          transform: `perspective(1000px) rotateX(${rotation.x}deg) rotateY(${rotation.y}deg) translate3d(${position.x}px, ${position.y}px, 0px)`,
          transition: isDragging ? 'none' : 'transform 0.6s cubic-bezier(0.4, 0, 0.2, 1)',
        }}
        onMouseDown={handleMouseDown}
        onMouseMove={isDragging ? handleMouseMove : undefined}
      >
        {/* Card Background Pattern */}
        <div className="absolute inset-0 opacity-10">
          <div className="absolute top-4 right-4 w-16 h-16 border-2 border-accent-purple rounded-full"></div>
          <div className="absolute bottom-4 left-4 w-8 h-8 bg-accent-cyan rounded-full opacity-50"></div>
        </div>

        {/* Profile Image Section */}
        <div className="relative p-6 pb-4">
          <div className="w-32 h-32 mx-auto rounded-2xl overflow-hidden border-4 border-white/10 shadow-xl">
            <img 
              src="/Rifki Aditya Hariyanto_Foto.jpg" 
              alt="Rifki Aditya Hariyanto" 
              className="w-full h-full object-cover"
              draggable={false}
            />
          </div>
        </div>

        {/* Card Content */}
        <div className="px-6 pb-6 text-center">
          {/* Name */}
          <h3 className="text-xl font-bold text-white mb-2">
            Rifki Aditya Hariyanto
          </h3>
          
          {/* Status */}
          <div className="flex items-center justify-center gap-2 mb-4">
            <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse shadow-lg shadow-green-500/50"></div>
            <span className="text-sm text-green-400 font-medium">Online</span>
          </div>

          {/* Info Cards */}
          <div className="space-y-3">
            <div className="flex items-center gap-3 p-2 bg-white/5 rounded-lg border border-white/10">
              <i className="fas fa-code text-accent-purple text-lg w-5"></i>
              <span className="text-sm text-white">Backend Developer</span>
            </div>
            
            <div className="flex items-center gap-3 p-2 bg-white/5 rounded-lg border border-white/10">
              <i className="fas fa-palette text-accent-blue text-lg w-5"></i>
              <span className="text-sm text-white">UI/UX Designer</span>
            </div>
            
            <div className="flex items-center gap-3 p-2 bg-white/5 rounded-lg border border-white/10">
              <i className="fas fa-graduation-cap text-accent-cyan text-lg w-5"></i>
              <span className="text-sm text-white">Telkom University</span>
            </div>
          </div>
        </div>

        {/* Shine Effect */}
        <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/5 to-transparent translate-x-[-100%] skew-x-12 lanyard-shine"></div>
      </div>
    </div>
  )
}

export default Lanyard
