import React from 'react';

interface LanyardProps {
  position?: [number, number, number];
  gravity?: [number, number, number];
  fov?: number;
  transparent?: boolean;
}

// Komponen Lanyard Sederhana (CSS Only)
const Lanyard: React.FC<LanyardProps> = () => {
  return (
    <div className="w-full h-full flex items-center justify-center">
      <div className="lanyard-card-wrapper">
        {/* Tali Lanyard */}
        <div className="lanyard-rope"></div>
        
        {/* ID Card */}
        <div className="lanyard-id-card">
          {/* Clip/Hook */}
          <div className="lanyard-clip"></div>
          
          {/* Card Content */}
          <div className="card-content">
            {/* Photo */}
            <div className="card-photo">
              <img 
                src="/Rifki Aditya Hariyanto_Foto.jpg" 
                alt="Rifki Aditya Hariyanto"
                className="w-full h-full object-cover"
              />
            </div>
            
            {/* Info */}
            <div className="card-info">
              <h3 className="card-name">RIFKI ADITYA HARIYANTO</h3>
              <p className="card-title">Full Stack Developer</p>
              <div className="card-divider"></div>
              <p className="card-role">Web Developer â€¢ UI/UX Designer</p>
              <p className="card-id">ID: DEV-2024-001</p>
            </div>
          </div>
          
          {/* Holographic effect overlay */}
          <div className="card-holographic"></div>
        </div>
      </div>
    </div>
  );
}

export default Lanyard;
