import React, { useRef, useState, Suspense } from 'react'
import { Canvas, useFrame, useLoader } from '@react-three/fiber'
import { Text, OrbitControls, Box, Sphere } from '@react-three/drei'
import { RigidBody, Physics } from '@react-three/rapier'
import * as THREE from 'three'

interface LanyardProps {
  position?: [number, number, number]
  gravity?: [number, number, number]
  fov?: number
  transparent?: boolean
}

function Card() {
  const meshRef = useRef<THREE.Mesh>(null!)
  const [hovered, setHovered] = useState(false)
  
  useFrame((state) => {
    if (meshRef.current) {
      meshRef.current.rotation.y = Math.sin(state.clock.elapsedTime * 0.5) * 0.1
      meshRef.current.rotation.x = Math.cos(state.clock.elapsedTime * 0.3) * 0.05
    }
  })

  return (
    <RigidBody type="dynamic" colliders="cuboid">
      <Box
        ref={meshRef}
        args={[3, 4, 0.1]}
        position={[0, 0, 0]}
        onPointerOver={() => setHovered(true)}
        onPointerOut={() => setHovered(false)}
      >
        <meshStandardMaterial 
          color={hovered ? "#a855f7" : "#1e293b"} 
          roughness={0.3}
          metalness={0.8}
        />
      </Box>
      
      {/* Profile Image Plane */}
      <mesh position={[0, 0.5, 0.051]}>
        <planeGeometry args={[2.5, 2]} />
        <meshBasicMaterial 
          color="#a855f7"
          transparent={true}
          opacity={0.8}
        />
      </mesh>
      
      {/* Text Elements - Using default font */}
      <Text
        position={[0, -1.2, 0.051]}
        fontSize={0.25}
        color="#ffffff"
        anchorX="center"
        anchorY="middle"
      >
        Rifki Aditya H.
      </Text>
      
      <Text
        position={[0, -1.6, 0.051]}
        fontSize={0.15}
        color="#a855f7"
        anchorX="center"
        anchorY="middle"
      >
        Backend Dev â€¢ UI/UX
      </Text>
      
      {/* Lanyard String */}
      <Box args={[0.02, 2, 0.02]} position={[0, 3, 0]}>
        <meshStandardMaterial color="#64748b" />
      </Box>
      
      {/* Lanyard Clip */}
      <Sphere args={[0.1]} position={[0, 4.2, 0]}>
        <meshStandardMaterial color="#a855f7" metalness={0.8} roughness={0.2} />
      </Sphere>
    </RigidBody>
  )
}

function Lights() {
  return (
    <>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} intensity={1} />
      <pointLight position={[-10, -10, -10]} intensity={0.5} color="#3b82f6" />
      <pointLight position={[5, -5, 5]} intensity={0.5} color="#06b6d4" />
    </>
  )
}

const Lanyard: React.FC<LanyardProps> = ({ 
  position = [0, 0, 8], 
  gravity = [0, -9.8, 0], 
  fov = 45, 
  transparent = true 
}) => {
  return (
    <div className="w-full h-full">
      <Canvas
        camera={{ position, fov }}
        gl={{ alpha: transparent, antialias: true }}
        style={{ background: transparent ? 'transparent' : '#0a0e27' }}
      >
        <Suspense fallback={null}>
          <Physics gravity={gravity}>
            <Lights />
            <Card />
            <OrbitControls 
              enablePan={false}
              enableZoom={false}
              enableRotate={true}
              autoRotate={false}
              minPolarAngle={Math.PI / 4}
              maxPolarAngle={Math.PI / 1.8}
              minAzimuthAngle={-Math.PI / 6}
              maxAzimuthAngle={Math.PI / 6}
            />
          </Physics>
        </Suspense>
      </Canvas>
    </div>
  )
}

export default Lanyard
